import React, { useContext } from "react";
import { GlobalContext } from "../context/GlobalState";

export const Popup = () => {
  const { popup } = useContext(GlobalContext);

  console.log(context);

  return (
    <div className="popup" hidden={popup === true ? "false" : "true"}>
        
    </div>);
};
