import React, { useContext, useEffect } from "react";
import { GlobalContext } from "../context/GlobalState";

export const Feed = () => {
  const { posts, getPosts } = useContext(GlobalContext);

  useEffect(() => {
    getPosts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []) // empty array for no loop

  // if (post[0]) {
  //   console.log(typeof post[0].date.getMinutes());
  //   }
  return (
    <div className="feed">
      {posts.map(postObject => {
        return (
          <div key={postObject._id}>
            {postObject.user}
            <br />
            <br />
            {postObject.text}
            <br />
            <br />
            {postObject.created.getHours()}:
            {postObject.created.getMinutes().toString().length < 2
              ? "0" + postObject.created.getMinutes()
              : postObject.created.getMinutes()}{" "}
            - {postObject.created.getDay()}.{postObject.date.getMonth()}.
            {postObject.created.getFullYear()}
            <br />
            <br />
            <hr />
            <br />
          </div>
        );
      })}



    </div>
  );
};
