export default (state, action) => {
  switch (action.type) {
    case "TOGGLE_POPUP":
      return {
        ...state,
        popup: action.payload
      };
    case "ADD_POST":
      return {
        ...state,
        post: [action.payload, ...state.post]
      };
    default:
      return state;
  }
};
