import React, { useContext, useState, useEffect, Suspense } from "react";
import { Route, Switch } from "react-router-dom";

import { Welcome } from "./Welcome";
import { Register } from "./Register";
import { Login } from "./Login";
import { NotFound } from "./NotFound";
import { Home } from "./Home";
import { Loading } from "./Loading";

import { GlobalContext } from "../context/GlobalState";

export const PageSwitch = () => {
  const { loggedIn, username, checkAuthenticated } = useContext(GlobalContext);
  const { authenticatedChecked, setAuthenticatedChecked } = useContext(
    GlobalContext
  );

  const checkAuthenticatedFunction = () => {
    let result = <Loading />;

    (async () => {
      try {
        const loggedInChecker = () => {
          console.log("loggedIn", loggedIn);
          console.log("authenticatedChecked", authenticatedChecked);
          if (loggedIn && username !== "") {
            result = (
              <Switch>
                <Route path="/" component={Home} />
                <Route component={NotFound} />
              </Switch>
            );
          } else if (!loggedIn && authenticatedChecked) {
            result = (
              <Switch>
                <Route path="/" exact component={Welcome} />
                <Route path="/register" component={Register} />
                <Route path="/login" component={Login} />
                <Route component={NotFound} />
              </Switch>
            );
          }
        };
        
        if (!authenticatedChecked) {
          await setTimeout(() => checkAuthenticated(), 1000);
          await setAuthenticatedChecked();
          await loggedInChecker();
        } else {
          await loggedInChecker();
        }

        //await loggedInChecker();
        console.log(result);
      } catch (error) {
        console.log(error);
      }
    })();
    return result;
  }; //, [authenticatedChecked, checkAuthenticated]);

  const loggedInChangePage = () => {
    /* if (loggedIn === true && username !== "") {
      return (
        <Switch>
          <Route path="/" component={Home} />
          <Route component={NotFound} />
        </Switch>
      );
    } else {
      return (
        <Switch>
          <Route path="/" exact component={Welcome} />
          <Route path="/register" component={Register} />
          <Route path="/login" component={Login} />
          <Route component={NotFound} />
        </Switch>
      );
    }*/
  };

  return <Suspense fallback={Loading}>{checkAuthenticatedFunction()}</Suspense>;
  //return <Suspense fallback={Loading}>{loggedInChangePage()}</Suspense>;
};
