import React, { useContext } from "react";
import { GlobalContext } from '../context/GlobalState';


export const Welcome = () => {
  const { changePage } = useContext(GlobalContext);

  const onClick = (page) => {
    changePage(page);
  }

  return (
      <div className="row mt-5">
        <div className="col-md-6 m-auto">
          <div className="card card-body text-center">
            <h1>
              <i className="fab fa-node-js fa-3x"></i>
            </h1>
            <p>Create an account or login</p>
            <button className="btn btn-primary btn-block mb-2" onClick={onClick("register")}>
              Register
            </button>
            <button className="btn btn-secondary btn-block" onClick={onClick("login")}>
              Login
            </button>
          </div>
        </div>
      </div>
  );
};
